import React, { Component } from 'react'

class DigitButton extends Component {
    displayDigit = () => {
        const { calculatorState, digit } = this.props;
        const newValue = `${calculatorState.CurrentOperand}${digit}`;
        const newParentState = { ...calculatorState, CurrentOperand: newValue };
        this.props.updateParentState(newParentState);
    }

  render() {
    return (
      <div>
        <button onClick={this.displayDigit}>{this.props.digit}</button>
      </div>
    )
  }
}

export default DigitButton
