import React, { Component } from 'react'

class FunctionButton extends Component {
    displayFunction = () => {
        this.props.onClick(this.props.function);
      };

    render() {
        const buttonClass = this.props.function === "AC" || this.props.function === "=" ? "span-two" : "";
        const gridColumn = this.props.function === "AC" ? "span 2" : "auto";
        return (
            <div>
                <button className={buttonClass} onClick={this.displayFunction} style={{gridColumn}}>{this.props.function}</button>
            </div>
        )
    }
}

export default FunctionButton
