import React, { Component } from 'react'

class OperationButton extends Component {
    displayDigit = () => {
        this.props.onClick(this.props.digit);
      };

    render() {
        return (
            <div>
            <button onClick={this.displayDigit}>{this.props.operator}</button>
            </div>
        )
    }
}

export default OperationButton
